<?php//// ����������� �������� ������.//include_once('m/model.php');include_once ('config/database.php');class C_Page extends C_Base{	//	// �����������.	//		public function action_index(){		$this->title .= '::Index';		$text = text_get();		$this->content = $this->Template('v/v_index.php', array('text' => $text));		}	public function action_auth() {	    $this->title .= '::Authorization';	    $text = '';        if($this->isPost())        {            $this->connect();            $text = login($_POST["name"], $_POST["password"], $this->connect);        }        if($_SESSION["auth"] == 1){            $name = $_SESSION["name"];            $this->content = "Hello, $name"."<br> <a href='index.php?c=auth&act=logout'>exit</a>";        } else {            $this->content = $this->Template('v/v_auth.php', array('text'=>$text));        };    }    public function action_logout() {	    session_destroy();	    header("Location: index.php");    }	public function action_edit(){		$this->title .= '::Changing';				if($this->isPost())		{			text_set($_POST['text']);			header('location: index.php');			exit();		}				$text = text_get();		$this->content = $this->Template('v/v_edit.php', array('text' => $text));			}}