<?phpsession_start();include "../config/database.php";function text_get(){	return file_get_contents('data/text.txt');}function text_set($text){	file_put_contents('data/text.txt', $text);}function login($user, $password, $connect){    $sql = "SELECT * FROM users";    $res = mysqli_query($connect, $sql);    while($row = mysqli_fetch_assoc($res)){        if($row["name"] === $user){            if($row["password"] == md5($password)){                $_SESSION["auth"] = 1;                $_SESSION["name"] = $user;            }        }        return "Incorrect data";    }}